/**
 * @param {UiObject} c
 * @param {string} password
 */
function inputPassword(c, password) {
  let rect = c.bounds();
  let points = password.split('').map((number) => getPoint(rect, +number))
  // @ts-ignore
  gesture.apply(null, [password.length * 500].concat(points));
}

/**
 * @param {Rect} rect
 * @param {number} number
 * @returns {[number, number]}
 */
function getPoint(rect, number) {
  let xunit = rect.width() / 6;
  let yunit = rect.height() / 6;
  let yc = Math.floor((number - 1) / 3);
  let xc = (number - 1) % 3;
  let left = rect.left;
  if (rect.width() > rect.height()) {
    let d = rect.width() - rect.height();
    left += d / 2;
  }
  return [
    left + ((2 * xc) + 1) * xunit,
    rect.top + ((2 * yc) + 1) * yunit
  ]
}

/**
 * @param {string} answer
 */
function checkIt (answer) {
  let checkbox = 'data:image/png;base64,'

  let img = images.captureScreen()
  let check = images.fromBase64(checkbox)
  // @ts-ignore
  let matchResult = images.matchTemplate(img, check)
  /** @type string[] */
  let optionStrs = []
  /** @type Point[] */
  let options = []
  matchResult.matches.forEach(function (x) {
    let str = x.point.x + ',' + x.point.y
    if (optionStrs.indexOf(str) === -1) {
      optionStrs.push(str)
      options.push(x.point)
    }
  })
  options.sort(function (a, b) {
    return a.y - b.y
  })
  /** @type {{[index: string]: number}} */
  let map = {
    'A': 0,
    'B': 1,
    'C': 2,
    'D': 3,
    'E': 4,
    'F': 5,
    'G': 6
  }
  let point = options[map[answer]]
  click(point.x + 10, point.y + 10)
  sleep(500)
}


function clickQAEntry () {
  let entry = 'data:image/png;base64,'
  let img = images.captureScreen()
  let j = images.fromBase64(entry)
  // @ts-ignore
  let matchResult = images.matchTemplate(img, j, { max: 1, threshold: 0.6 })
  // @ts-ignore
  let p = matchResult.matches[0].point
  click(p.x + 10, p.y + 10)
  sleep(500)
}

function confirmCheck () {
  let join = 'data:image/png;base64,'

  let img = images.captureScreen()
  let j = images.fromBase64(join)
  // @ts-ignore
  let matchResult = images.matchTemplate(img, j, { max: 1 })
  // @ts-ignore
  let p = matchResult.matches[0].point
  click(p.x + 10, p.y + 10)
  sleep(500)
}


export default app;
